import findTransportDirection.HomePage;import findTransportDirection.TimetablePage;import org.junit.jupiter.api.Test;import org.openqa.selenium.support.PageFactory;import static org.junit.jupiter.api.Assertions.assertTrue;class YandexTests extends Settings {    private TimetablePage timetablePage = PageFactory.initElements(driver, TimetablePage.class);    private HomePage homePage = PageFactory.initElements(driver, HomePage.class);    @Test    void findTheWay() {        homePage.open();        homePage.whereFrom("Кемерово");        homePage.whereTo("Москва");        homePage.departureDate("22 июля");        homePage.searchButtonClick();    }    @Test    void durationTest() {        findTheWay();        assertTrue(timetablePage.duration());    }    @Test    void transportIconTest() {        findTheWay();        assertTrue(timetablePage.transportIcon());    }    @Test    void tittleTest() {        findTheWay();        assertTrue(timetablePage.tittle());    }}