package findTransportDirection;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.support.ui.ExpectedConditions;import org.openqa.selenium.support.ui.WebDriverWait;public class HomePage {    private WebDriver driver;    private WebDriverWait wait;    public HomePage(WebDriver driver) {        this.driver = driver;        wait = new WebDriverWait(driver, 10);    }    public void open() {        driver.get("https://rasp.yandex.ru/");    }    private By whereFromLocator = By.cssSelector("[placeholder=\"Откуда\"]");    private By whereToLocator = By.cssSelector("[placeholder=\"Куда\"]");    private By departureDateLocator = By.cssSelector("[placeholder=\"Когда\"]");    private By searchButton = By.cssSelector("[class=\"Button SearchForm__submit\"]");    private By newPage = By.cssSelector("[class=\"SearchTitle\"]");    public void whereFrom(String city) {        driver.findElement(whereFromLocator).clear();        driver.findElement(whereFromLocator).sendKeys(city);    }    public void whereTo(String city) {        driver.findElement(whereToLocator).sendKeys(city);    }    public void departureDate(String date) {        driver.findElement(departureDateLocator).clear();        driver.findElement(departureDateLocator).sendKeys(date);    }    public void searchButtonClick() {        driver.findElement(searchButton).click();        wait.until(ExpectedConditions.visibilityOfElementLocated(newPage));    }}